<template>
  <div>
	  <Layout-Page-Title :title="'Tin tức & Sự kiện'"/>
	  <div class="row wrapper my-5 position-relative">
		<nuxt-link :to="'/blog/' + item.slug" class="card col-12 col-xs-6 col-md-3 p-0" role="button" v-for="item in posts" :key="item.id">
		  <img :src="item.image" class="card-img-top" alt="..." />
		  <div class="card-body">
			<h5 class="card-title">{{item.title}}</h5>
			<p class="card-text">
			  {{item.seo_des}}
			</p>
		  </div>
		  <div @click="test(item.date_created)" class="position-absolute top-0 start-0 p-2 m-1 rounded bg-dark text-white">
			  {{item.date_created | formatDate}}
		  </div>
		</nuxt-link>
	  </div>
	  
  </div>
</template>
<script>
export default {
	filters : {
	formatDate(value) {
		var date = value.getDate();
		var month = value.getMonth();
		var dateStr = date;
		var monthStr = month + 1;
		if(month<10)
		{
			monthStr = `0${month + 1}`;
		}
		if(date<10)
		{
			dateStr = `0${date}`;
		}
		return `${dateStr}/${monthStr}/${value.getFullYear()}`
	}
	},
  data() {
    return {
      posts: [
        {
          id: 1,
          title: "Khuyến mại mùa xuân",
		  seo_des : "Hàng ngàn các khuyến mại, mã giảm giá của Thúy Hằng store",
          date_created: new Date("04/12/2000"),
		  slug : '/khuyen-mai-mua-xuan',
          image:
            "data:image/jpeg;base64,",
        },
      ],
    };
  },
  methods: {
	   test(value) {
		   this.$router.push(`/blog/${value}`)
	   }
  },
};
</script>
<style scoped>
.datetime {
	position: absolute;
}
</style>
